import './site_cart.css'
import { decreaseItemQuantity, increaseItemQuantity, calculateTotal} from '../../features/cartSlice'
import { useSelector,useDispatch } from "react-redux"
import { useEffect } from 'react'

const SiteCart = () => {
    const dispatch = useDispatch()
    const allCarts = useSelector((state)=>state.cartSlice)

    useEffect(()=>{
        dispatch(calculateTotal)
    },[])
    
    return (
        <div className="site-cart-body">
            <div className="item-quantity">
                <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-cart-plus" viewBox="0 0 16 16">
                    <path d="M9 5.5a.5.5 0 0 0-1 0V7H6.5a.5.5 0 0 0 0 1H8v1.5a.5.5 0 0 0 1 0V8h1.5a.5.5 0 0 0 0-1H9V5.5z" />
                    <path d="M.5 1a.5.5 0 0 0 0 1h1.11l.401 1.607 1.498 7.985A.5.5 0 0 0 4 12h1a2 2 0 1 0 0 4 2 2 0 0 0 0-4h7a2 2 0 1 0 0 4 2 2 0 0 0 0-4h1a.5.5 0 0 0 .491-.408l1.5-8A.5.5 0 0 0 14.5 3H2.89l-.405-1.621A.5.5 0 0 0 2 1H.5zm3.915 10L3.102 4h10.796l-1.313 7h-8.17zM6 14a1 1 0 1 1-2 0 1 1 0 0 1 2 0zm7 0a1 1 0 1 1-2 0 1 1 0 0 1 2 0z" />
                </svg>
                <span className='quantity'>{allCarts.cartItems.length} Item</span>
            </div>
            <div className='amount'>
                <span className='amount-text'>$10</span>
            </div>
        </div>
    )
}

export default SiteCart
