import { useEffect, useState } from 'react'
import './adminSidebar.css'
const AdminSidebar = () => {

    let navData = [
        {
            id: 1,
            title: 'product',
            subNav: [
                {
                    subNavId: 11,
                    subNavTitle: 'add product'
                },

                {
                    subNavId: 12,
                    subNavTitle: 'product list'
                }
            ]
        },

        {
            id: 2,
            title: 'post',
            subNav: [
                {
                    subNavId: 11,
                    subNavTitle: 'add product'
                },

                {
                    subNavId: 12,
                    subNavTitle: 'product list'
                }
            ]
        }
    ]

    const [dropdown, setDropdown] = useState([])
    const [show, setShow] = useState(false)
    const [navigation, setNavigation] = useState(navData)



    useEffect(() => {
        showChildBar()
    }, [])

    let showChildBar = () => {
        setShow(!show)

        if (show) {
            let data = [{ name: 'tokon' }, { name: 'akash' }]
            setDropdown(data)
            console.log(dropdown)
        } else {
            let data = []
            setDropdown(data)
            console.log(dropdown)
        }

    }

    return (
        <div className="side-bar">

            <div className="search-by-other">
                <p>Admin Dashboard</p>

                {
                    navigation.map((nav) => (
                        <ul key={nav.id}>
                            <li className='product-button' onClick={showChildBar} > {nav.title}  <i className="fa-solid fa-chevron-right nav-icon"  ></i>
                                {nav.subNav.map((item) => (
                                    <ul key={item.subNavId}> <li> {item.subNavTitle} </li> </ul>
                                ))}

                            </li>
                        </ul>
                    ))
                }



            </div>




        </div>
    )
}

export default AdminSidebar
