import { useEffect, useState } from 'react'
import './adminSidebar.css'
const AdminSidebar = ()=>{

    let navData = [
        {
            id:1,
            title:'product',
            subNav:[
                {
                    id:11,
                    title:'add product'
                },

                {
                    id:12,
                    title:'product list'
                }
            ]
        },
        {
            id:2,
            title:'post',
        }
    ]

    const [dropdown,setDropdown] = useState([])
    const [show,setShow]=useState(false)
    const [navigation,setNavigation] = useState(navData)



    useEffect(()=>{
        showChildBar()
    },[])

    let showChildBar = ()=>{ 
        setShow(!show)
        
        if(show){
            let data = [{name:'tokon'},{name:'akash'}]
            setDropdown(data)
            console.log(dropdown)
        }else{
            let data = []
            setDropdown(data)
            console.log(dropdown)
        }

    }

    return (
        <div className="side-bar">

                <div className="search-by-other">
                    <p>Admin Dashboard</p>

                    {
                        navigation.map((nav)=>(
                            <p>{nav}</p>
                        ))
                    }
                    <ul>
                        <li className='product-button' onClick={showChildBar} > Product  <i className="fa-solid fa-chevron-right nav-icon"  ></i>
                           {dropdown.map((item)=>(
                            <ul key={item.name}> <li> {item.name} </li> </ul>
                           ))}

                         </li>      
                    </ul>
                    

                </div>


          
            
        </div>
    )
}

export default AdminSidebar
