import { useEffect, useState } from 'react'
import './adminSidebar.css'
const AdminSidebar = () => {

    let navData = [
        {
            id: 1,
            title: 'product',
            subNav: [
                {
                    subNavId: 11,
                    navId:1,
                    subNavTitle: 'add product'
                },

                {
                    subNavId: 12,
                    navId:1,
                    subNavTitle: 'product list'
                }
            ]
        },

        {
            id: 2,
            title: 'post',
            subNav: [
                {
                    subNavId: 11,
                    id: 2,
                    subNavTitle: 'add post'
                },

                {
                    subNavId: 12,
                    id: 2,
                    subNavTitle: 'post list'
                }
            ]
        }
    ]

    const [dropdown, setDropdown] = useState([])
    const [show, setShow] = useState(false)
    const [navigation, setNavigation] = useState(navData)



    // useEffect(() => {
    //     showChildBar()
    // }, [])

    // let showChildBar = () => {
    //     setShow(!show)

    // }

    let showHandle = (navId)=>{
        console.log(navId)
    }

    return (
        <div className="side-bar">

            <div className="search-by-other">
                <p>Admin Dashboard</p>

                {
                    navigation.map((nav,index) => (
                        <ul key={nav.id}>
                            <li className='product-button' onClick={()=>showHandle(nav.id)} > {nav.title}  <i className="fa-solid fa-chevron-right nav-icon"  ></i>
                                {
                                
                                show?nav.subNav.map((item) => (
                                    <ul key={item.subNavId}>  <li>{item.subNavTitle} </li> </ul>
                                )):''
                                
                                }

                            </li>
                        </ul>
                    ))
                }



            </div>




        </div>
    )
}

export default AdminSidebar
