import {createApi, fetchBaseQuery} from "@reduxjs/toolkit/query/react"
import baseUrlWithHeaders from "../baseUrlWithHeaders"
export const permissionApi = createApi({
    reducerPath:'permissionApi',
    baseQuery:fetchBaseQuery({baseUrl:'http://localhost:5000',}),
    tagTypes:'Permission',
    endpoints:(builder)=>({

        getAllPermission : builder.query({
            query:()=>'permission',
            providesTags:['Permission']
            
        }),

        addNewPermission : builder.mutation({
            query:(permissionData)=>({
                url:'/permission',
                method:'POST',
                data:permissionData
            }),
            invalidatesTags:['Permission']
        })

        

    })

})

export const {useGetAllPermissionQuery,useAddNewPermissionMutation} = permissionApi
