import {createApi, fetchBaseQuery} from "@reduxjs/toolkit/query/react"
import baseUrlWithHeaders from "../baseUrlWithHeaders"
export const menuApi = createApi({
    reducerPath:'menuApi',
    baseQuery:fetchBaseQuery({baseUrl:'http://localhost:5000',}),
    tagTypes:'menuTag',
    endpoints:(builder)=>({

        getAllTest : builder.query({
            query:()=>'menu',
            providesTags:['menuTag']
            
        }),

        addNewMenu : builder.mutation({
            query:(menuData) => ({
                url:'/menu',
                method:'POST',
                body:menuData
            }),
            invalidatesTags:['menuTag']
        }),

    })

})

export const {useAddNewMenuMutation, useGetAllTestQuery} = menuApi
