import {createSlice} from '@reduxjs/toolkit'

const initialState = {
    userInfo:{},
    token:null
}
const authSlice = createSlice({
    name:'authSlice',
    initialState,
    reducers:{
        setUser:(state,payload)=>{
            state.token = payload.token
        }
    }
})

export const {setUser} = authSlice.actions

export default authSlice.reducer
